var root = '../../';
var paths = require(root + 'config/paths.json');

module.exports = {
    app: function() {
        return this.get(paths.app);
    },
    pkg: function() {
    	return this.get(paths.pkg);
    },
    routeConfig: function() {
        return this.get(paths.node.routes);
    },
    util: function(module) {
        return this.get(paths.node.util + '/' + module);
    },
    controller: function(module) {
        return this.get(paths.node.controllers + '/' + module);
    },
    /**
     * Helper method
     */
    get: function(src) {
        return require(root + src);
    }
};
