var root = '../../';
var paths = require(root + 'config/paths.json');

module.exports = {
    app: function() {
        return this.get(paths.app);
    },
    routes: function() {
        return this.get(paths.node.routes);
    },
    get: function(src) {
        return require(root + src);
    }
};

